pragma solidity ^0.7.0;

contract KriptyFide {
    
    KriptyInterfaceFide public Krypti;
    mapping (address => uint256) private claimedTokens;

    constructor(address Krypti_) public {
        Krypti = KriptyInterfaceFide(Krypti_);
    }

    function claimTokens(address to, uint256 amount) internal {
        uint256 claimedTokensfrom  = claimedTokens[to];
        Krypti.transfer(to, (amount - claimedTokensfrom));
        claimedTokens[to] = claimedTokensfrom + amount;
    }

}

interface KriptyInterfaceFide {
    function transfer(address recipient, uint amount) external;
}
